import React, { useRef } from 'react';
import './App.css';
import './TimeProgress.css';
import { Button } from 'antd';

interface Props {
  sessions: { title: string; time: number }[];
}

const App: React.FC<Props> = ({ sessions }) => {
  const containerRef = useRef<HTMLDivElement>(null);
  // const timerId = useRef();

  const handleStart = () => {
    if (!containerRef.current) return;

    let maxTime = 120;
    let currentTime = 0;
    const timerId = setInterval((arg) => {
      if (currentTime >= maxTime) {
        clearInterval(timerId);
        currentTime = 120;
      }
      containerRef.current!.style.background = `linear-gradient(to right, red 10%, white 10%)`;
    }, 1000);
  };

  return (
    <div className="time-progress-container">
      <div>Timer Progress</div>
      <div className="progress-container" ref={containerRef}>
        {sessions.map((session) => {
          const randomColor = Math.floor(Math.random() * 16777215).toString(16);
          console.log('ðŸš€  randomColor:', randomColor);
          const style: React.CSSProperties = {
            width: `${(session.time / 120) * 100}%`,
            backgroundColor: `#${randomColor}`,
          };

          return (
            <div className="session-item" style={style}>
              <span>{session.title}</span>
            </div>
          );
        })}
      </div>
      <Button onClick={handleStart}>Start</Button>
    </div>
  );
};

export default App;
