import React, { useRef, useState } from 'react';
import './App.css';
import './TimeProgress.css';
import { Button, Space } from 'antd';

interface Props {
  sessions: { title: string; time: number; color: string }[];
}

const App: React.FC<Props> = ({ sessions }) => {
  const containerRef = useRef<HTMLDivElement>(null);
  const [ongoing, setOngoing] = useState(false);

  const handleStart = () => {
    if (!containerRef.current) return;
    setOngoing(true)

    let maxTime = 120;
    let currentTime = 0;
    const timerId = setInterval(() => {
      if (currentTime >= maxTime) {
        clearInterval(timerId);
        currentTime = 120;
      }

      const percent = (++currentTime / maxTime) * 100;
      containerRef.current!.style.background = `linear-gradient(to right, red ${percent}%, white ${percent}%)`;
    }, 1000);
  };

  return (
    <div className="time-progress-container">
      <div>Time Progress Bar</div>
      <div className="progress-container" ref={containerRef}>
        {sessions.map((session) => {
          const style: React.CSSProperties = {
            width: `${(session.time / 120) * 100}%`,
            backgroundColor: session.color,
          };

          return (
            <div className="session-item" style={style}>
              <p className="title">{session.title}</p>
              <span className="time">{session.time}mins</span>
            </div>
          );
        })}
      </div>
      <Space size="large">
        {ongoing ? (
          <Button type="primary" onClick={handleStart}>
            Take a Break
          </Button>
        ) : (
          <Button type="primary" onClick={handleStart}>
            Start
          </Button>
        )}
      </Space>
    </div>
  );
};

export default App;
