import React, { useRef, useState } from 'react';
import './App.css';
import './TimeProgress.css';
import { Button, Space } from 'antd';

interface Props {
  sessions: { title: string; time: number; color: string }[];
  courseDuration?: number; // minute
}

const App: React.FC<Props> = ({ sessions, courseDuration = 120 }) => {
  const maxTime = 120; // 120 * 60;

  const containerRef = useRef<HTMLDivElement>(null);
  const [ongoing, setOngoing] = useState(false);

  const handleStart = () => {
    if (!containerRef.current) return;

    setOngoing(true);

    // let maxTime = 120;
    let currentTime = 0;
    const timerId = setInterval(() => {
      if (currentTime >= maxTime) {
        clearInterval(timerId);
        currentTime = 120;
      }

      const percent = (++currentTime / maxTime) * 100;
      containerRef.current!.style.background = `linear-gradient(to right, red ${percent}%, white ${percent}%)`;
    }, 1000);
  };

  const handleTakeABreak = () => {
    setOngoing(false);
  };

  const timeContent = () => {
    const currentTime = '';
    const totalTime = '';
    return (
      <div className="time-content">
        <span>{currentTime}</span>
        <span>/</span>
        <span>{totalTime}</span>
      </div>
    );
  };

  return (
    <div className="time-progress-container">
      <div>Time Progress Bar</div>

      <div>Total time: 120mins</div>
      <div className="progress-container" ref={containerRef}>
        {sessions.map((session, i) => {
          const style: React.CSSProperties = {
            width: `${(session.time / 120) * 100}%`,
            backgroundColor: session.color,
          };

          return (
            <div key={i} className="session-item" style={style}>
              <p className="title">{session.title}</p>
              <span className="time">{session.time}mins</span>
            </div>
          );
        })}
      </div>
      {timeContent}
      <Space size="large">
        {ongoing ? (
          <Button type="primary" onClick={handleTakeABreak}>
            Take a Break
          </Button>
        ) : (
          <Button type="primary" onClick={handleStart}>
            Let's start the class.
          </Button>
        )}
      </Space>
    </div>
  );
};

export default App;
